import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { IAnalisis } from '../interfaces/Ianalisis';
import { Observable, debounceTime, distinctUntilChanged } from 'rxjs';
import { HttpHeaders, HttpClient } from '@angular/common/http';
import { AnalisisService } from '../services/analisis-service.service';

@Component({
  selector: 'app-buscador',
  templateUrl: './buscador.component.html',
  styleUrls: ['./buscador.component.css']
})
export class BuscadorComponent implements OnInit{

  const element = event.currentTarget as HTMLInputElement
const value = element.value

  @ViewChild('buscador') buscador!: ElementRef;

  ngAfterViewInit() {
    this.buscador.nativeElement.addEventListener('keyup', (e: KeyboardEvent) => {
      if (e.target.matches('#buscador-analisis')) {
        if (e.key === 'Escape') {
          this.buscador.nativeElement.value = '';
        }
        document.querySelectorAll('.btn').forEach((analisis: Element) => {
          const btnText = analisis.textContent?.toLowerCase() || '';
          const searchValue = this.buscador.nativeElement.value?.toLowerCase() || '';
          if (btnText.includes(searchValue)) {
            analisis.classList.remove('filtro');
          } else {
            analisis.classList.add('filtro');
          }
        });
      }
    });
  }


  ngOnInit(): void {

  }


}
